71/46
sample program
#include <gl/glut.h>

void main( int argc, char* argv[]){
	glutinit (&argc, argv);
 	glutinitdisplaymode (glut_single | glut_rgb );
	glutinitwindowsize (250, 250);
	glutinitwindowposition (100, 100);
 	glutcreatewindow ( "hello");

 	init();	

 	glutdisplayfunc ( display ); 

 	glutmainloop();
}
/* opengl initialization */
/* callback function */

72/46
glut callback functions回调函数
routine to call when something happens
window resized, user input, window needs drawing, etc…
用glut注册回调函数“register” callbacks 
 
 //窗口变化需要重绘时将调用display绘制
 glutdisplayfunc( display );
 glutkeyboardfunc( keyboard );
 //鼠标点击时将调用mouse函数进行处理
 glutmousefunc( mouse );

opengl initialization
void init (void) { 
	/* select clearing (background) color */ 
	glclearcolor (0.0, 0.0, 0.0, 0.0);
 /* initialize viewing values */ 
	glmatrixmode(gl_projection); 
	glloadidentity(); 
 glfloat size = 1.0;
 glortho(0.0, size, 0.0, size, -1.0, 1.0); 
}

dispaly
void display(void) { 
 glclear (gl_color_buffer_bit); /* clear all pixels */ 
 /* draw white polygon (rectangle) */ 
 glcolor3f (1.0, 1.0, 1.0); 
 glbegin(gl_polygon); 
 glvertex3f (p1[0], p1[1], 0.0); 
 glvertex3f (p2[0], p1[1], 0.0); 
 glvertex3f (p2[0], p2[1], 0.0);
 glvertex3f (p1[0], p2[1], 0.0); 
 glend(); 
 glflush (); /* don't wait! */ 
}

include files and data
#include <gl/glut.h> 
#include <stdlib.h>

typedef glfloat point3d[3];
point3d p1,p2;
p1[0] = 0.25; p1[1] = 0.25; p1[2] = 1;
p2[0] = 0.75; p2[1] = 0.75; p2[2] = 1;
